mapper.select:
	  SELECT IFNULL((pro_inwon - minus_inwon), 0) 
  	  FROM program_capacity 
  	  WHERE pro_id = #{pro_id} 
      AND date = #{date}

select * from program_capacity
where date=#{reserve_date}
and pro_id=#{pro_id}

mapper.update:
update program_capacity
set minus_inwon=minus_inwon+#{p_inwon}
where pro_id=#{pro_id}
and date=#{reserve_date}
